export const ItemsVariants = {
    start: { opacity: 0, scale:0.2 },
    stop: { 
        opacity: 1,
        scale:1,
        transition: { 
            duration: 1,
            when: "beforeChildren", 
            staggerChildren: 1,   // Makes each child wait for previous child for x seconds, beofre the next child animation
        }
    },
}

export const itemVariants= {
    start: {x: '-100vw'},
    stop: {
        x: 0,
        transition: {type: 'spring'}
    },
}
